// src/utils/api.js
export const fetchResponse = async (prompt) => {
  try {
    const response = await fetch("http://localhost:5000/api/courses/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ topic: prompt }), // send 'topic' since backend expects it
    });

    if (!response.ok) {
      throw new Error("Network response was not ok");
    }

    const data = await response.json();
    return data; // full course object: {title, description, modules}
  } catch (error) {
    console.error(error);
    return null;
  }
};
