import React, { useState } from 'react';
import PromptForm from '../components/PromptForm';
import CoursePreview from '../components/CoursePreview';

export default function Home() {
  const [course, setCourse] = useState(null);

  return (
    <div style={{ padding: 24, maxWidth: 900, margin: '0 auto' }}>
      <header style={{ marginBottom: 18 }}>
        <h1>Text-to-Learn — Prototype (Milestone 1)</h1>
        <p style={{ color: '#666' }}>Enter a topic to generate a mock course outline (prototype).</p>
      </header>

      <PromptForm onResult={setCourse} />

      {course ? (
        <CoursePreview course={course} />
      ) : (
        <div style={{ color: '#777', marginTop: 12 }}>No course yet — try a topic above.</div>
      )}
    </div>
  );
}
