// src/pages/Home.jsx
import React from 'react';
import './Home.css';
import CoursePreview from '../components/CoursePreview';
import PromptForm from '../components/PromptForm';

const Home = () => {
  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Welcome to Text-to-Lear</h1>
        <p>Your AI-powered learning companion</p>
      </header>

      <PromptForm />

      <div className="course-preview-main">
        <div className="course-card">
          <CoursePreview title="Course 1" description="Intro to AI" />
          <CoursePreview title="Course 2" description="React Basics" />
          <CoursePreview title="Course 3" description="Node.js API" />
        </div>
      </div>
    </div>
  );
};

export default Home;
