// src/components/PromptForm.jsx
import React, { useState } from 'react';
import { fetchResponse } from '../utils/api';

const PromptForm = () => {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await fetchResponse(prompt);
    setResponse(result);
  };

  return (
    <div className="mb-6">
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt here..."
          className="border p-2 rounded h-32"
        />
        <button type="submit" className="bg-teal-500 text-white p-2 rounded">
          Submit
        </button>
      </form>
      {response && <div className="mt-4 p-2 border rounded">{response}</div>}
    </div>
  );
};

export default PromptForm;
