// src/components/PromptForm.jsx
import React, { useState } from 'react';
import { fetchResponse } from '../utils/api';

const PromptForm = ({ onResult }) => {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const result = await fetchResponse(prompt);
      if (onResult) onResult(result); // Pass result to Home
    } catch (err) {
      console.error(err);
      setError('Error fetching response. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mb-6">
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt here..."
          className="border p-2 rounded h-32"
        />
        <button
          type="submit"
          className="bg-teal-500 text-white p-2 rounded"
          disabled={loading}
        >
          {loading ? 'Generating...' : 'Submit'}
        </button>
      </form>
      {error && <p className="text-red-500 mt-2">{error}</p>}
    </div>
  );
};

export default PromptForm;
