import React from 'react';

export default function CoursePreview({ course }) {
  if (!course) return null;
  return (
    <div style={{ border: '1px solid #ddd', padding: 16, borderRadius: 6 }}>
      <h2 style={{ margin: '4px 0' }}>{course.title}</h2>
      <p style={{ color: '#555' }}>{course.description}</p>

      {course.modules?.map((m, idx) => (
        <section key={idx} style={{ marginTop: 12 }}>
          <h3 style={{ margin: '6px 0' }}>{idx + 1}. {m.title}</h3>
          <ul>
            {m.lessons?.map((lesson, i) => (
              <li key={i}>{lesson}</li>
            ))}
          </ul>
        </section>
      ))}
    </div>
  );
}
