import React, { useState } from 'react';
import { generateCourse } from '../utils/api';

export default function PromptForm({ onResult }) {
  const [topic, setTopic] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    if (!topic.trim()) {
      setError('Enter a topic.');
      return;
    }
    setLoading(true);
    try {
      const data = await generateCourse(topic);
      onResult(data);
    } catch (err) {
      setError(err.message || 'Error generating course');
    } finally {
      setLoading(false);
    }
  }

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 16 }}>
      <input
        value={topic}
        onChange={(e) => setTopic(e.target.value)}
        placeholder="e.g. Intro to React Hooks"
        style={{ padding: 8, width: '60%', marginRight: 8 }}
      />
      <button type="submit" disabled={loading} style={{ padding: '8px 12px' }}>
        {loading ? 'Generating…' : 'Generate'}
      </button>
      {error && <div style={{ color: 'crimson', marginTop: 8 }}>{error}</div>}
    </form>
  );
}
