import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PromptForm from '../components/PromptForm';
import CoursePreview from '../components/CoursePreview';
import './Home.css';

export default function Home() {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  function handleNewCourse(course) {
    const newCourse = { ...course, id: Date.now().toString() }; // temporary ID
    setCourses((prev) => [...prev, newCourse]);
    setSelectedCourse(newCourse);
  }

  return (
    <div className="home-container">
      {/* Navigation */}
      <nav className="navbar">
        <h2 className="logo">ðŸ“˜ Text-to-Learn</h2>
        <div className="nav-links">
          <Link to="/login">Login</Link>
          <Link to="/signup">Signup</Link>
        </div>
      </nav>

      {/* Header */}
      <header className="home-header">
        <h1>Welcome to Text-to-Learn</h1>
        <p>Generate structured courses instantly, or pick from your saved list below.</p>
      </header>

      {/* PromptForm */}
      <PromptForm onResult={handleNewCourse} />

      <div className="home-layout">
        {/* Course List */}
        <aside className="course-list">
          <h3>ðŸ“‚ Your Courses</h3>
          {courses.length === 0 ? (
            <p>No courses yet. Generate one above.</p>
          ) : (
            <ul>
              {courses.map((c) => (
                <li key={c.id}>
                  <button
                    className={selectedCourse?.id === c.id ? 'active' : ''}
                    onClick={() => setSelectedCourse(c)}
                  >
                    {c.title}
                  </button>
                  <span className="view-link">
                    <Link to={`/course/${c.id}`}>View</Link>
                  </span>
                </li>
              ))}
            </ul>
          )}
        </aside>

        {/* Course Preview */}
        <main className="course-preview">
          {selectedCourse ? (
            <CoursePreview course={selectedCourse} />
          ) : (
            <p>Select a course from the list or generate a new one.</p>
          )}
        </main>
      </div>
    </div>
  );
}
